    private void btnupperActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnupperActionPerformed
    String uppercase_string;
    
    uppercase_string = (txtupper_string.getText());
    
    // displays all the charcters in the inputed string as uppercase
    lblupper_result.setText(String.valueOf(uppercase_string.toUpperCase()));
    }//GEN-LAST:event_btnupperActionPerformed

    private void btnequalsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnequalsActionPerformed
    String equals_string1;
    String equals_string2;
    
    equals_string1 = (txtequals_string1.getText());
    equals_string2 = (txtequals_string2.getText());
    
    // displays a statement if the first string is equals to the second one, and displays a different statement if the first string isn't equal to the second
    if (equals_string1.equals(equals_string2)) {
    lblequals_result.setText(String.valueOf("Both Strings are identical."));    
    }
    else {
    lblequals_result.setText(String.valueOf("Both Strings are not identical."));   
    }
    }//GEN-LAST:event_btnequalsActionPerformed

    private void btncontainsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncontainsActionPerformed
    String contains_string;
    String contains_char;
    
    contains_string = (txtcontains_string.getText().toLowerCase());
    contains_char = (txtcontains_char.getText().toLowerCase());
    
    // displays a statement if the inputed string contains the inputed character, and displays a different statement if it doesn't
    if (contains_string.contains(contains_char)) {
    lblcontains_result.setText(String.valueOf(contains_string + " contains the character, " + contains_char));  
    }
    else {
    lblcontains_result.setText(String.valueOf(contains_string + " does not contain the character, " + contains_char));    
    }
    }//GEN-LAST:event_btncontainsActionPerformed

    private void btnreplaceActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnreplaceActionPerformed
    String replace_string;
    
    replace_string = (txtreplace_string.getText());
    
    // displays the inputed string except all "a" characters are replaced with "x" characters
    lblreplace_result.setText(String.valueOf(replace_string.replace("a","x")));
    }//GEN-LAST:event_btnreplaceActionPerformed

